import { useState } from 'react';

const images = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
];

export default function Carousel() {
    const [current, setCurrent] = useState(0);
    const length = images.length;

    const nextSlide = () => {
        setCurrent(current === length - 1 ? 0 : current + 1);
    };

    const prevSlide = () => {
        setCurrent(current === 0 ? length - 1 : current - 1);
    };

    return (
        <div className='relative w-full max-w-3xl mx-auto mt-8 pr-5 pl-5'>
        <div className="relative w-full max-w-3xl mx-auto mt-8 ">
            <div className="overflow-hidden rounded-lg shadow-lg">
                <img
                    src={images[current]}
                    alt={`Slide ${current}`}
                    className="w-full h-100 object-cover transition duration-50 ease-in-out "
                />
            </div>

            {/* Navigation Buttons */}
            <button
                onClick={prevSlide}
                className="absolute top-1/2 left-0 transform -translate-y-1/2 bg-white bg-opacity-70 hover:bg-opacity-100 text-gray-800 font-bold px-4 py-2 "
            >
                ‹
            </button>
            <button
                onClick={nextSlide}
                className="absolute top-1/2 right-0 transform -translate-y-1/2 bg-white bg-opacity-70 hover:bg-opacity-100 text-gray-800 font-bold px-4 py-2"
            >
                ›
            </button>

            {/* Dots */}
            <div className="flex justify-center mt-4 space-x-2">
                {images.map((_, index) => (
                    <button
                        key={index}
                        onClick={() => setCurrent(index)}
                        className={`w-3 h-3 rounded-full ${index === current ? 'bg-blue-500' : 'bg-gray-300'
                            }`}
                    ></button>
                ))}
            </div>
            </div>
        </div>
    );
}
